var contactUsLink = document.querySelector('.contact-us-button'),
  contactPopup = document.querySelector('.contact-us-popup'),
  closeForm = contactPopup.querySelector('.popup-close');
contactUsLink.addEventListener('click', function(o) {
  o.preventDefault(), contactPopup.classList.add('popup-show');
}),
  closeForm.addEventListener('click', function(o) {
    o.preventDefault(), contactPopup.classList.remove('popup-show');
  }),
  window.addEventListener('keydown', function(o) {
    o.preventDefault(),
      27 === o.keyCode &&
        contactPopup.classList.contains('popup-show') &&
        contactPopup.classList.remove('popup-show');
  });
